import React from 'react';

class Items extends React.Component {
  render(){
    return (
      <li>
        {this.props.name},
        ${this.props.pirce};
      </li>
    )
  }
}

class App extends React.Component {
  render(){
    return(
      <div>
        <h1>Hello React</h1>
        {/* or can write <Item/> */}
        <Items name = "apple" pirce = "0.99"/>
        <Items name = "orange" pirce = "0.89"/>
      </div>
    )
  }
}

export default App;
